import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Table, Button, Container, Modal, ModalBody, ModalHeader, FormGroup, ModalFooter} from 'reactstrap';
const data=[
  {id: 1, name: "Adriana Guilindro", email: "aguilind@espol.edu.ec", phone: "09895965431", address: "Calle falsa 123"},
  {id: 2, name: "Pedro Pascal", email: "pedrop@espol.edu.ec", phone: "09831234567", address: "Calle principal 345"},
  {id: 3, name: "Oscar Hernandez", email: "oscarerd@espol.edu.ec", phone: "0982345678", address: "Calle lineal 567"},
  {id: 4, name: "Miguel O'Hara", email: "miguelha@espol.edu.ec", phone: "0983456789", address: "Calle esperanza 897"},
  {id: 5, name: "Julieta Joven", email: "julietajo@espol.edu.ec", phone: "0985678934", address: "Calle global 986"},

];
class App extends React.Component {
  state={
    data: data
  }
  render(){
    return (
    <>
    <Container>
      <br><Button color="primary">Insertar nuevo usuario</Button></br>


    <Table>
      <thead><tr>
        <th>Id</th>
        <th>Nombre</th>
        <th>Correo</th>
        <th>Telefono</th>
        <th>Direccion</th>
        <th>Acciones</th>
      </tr>
      </thead>
      <tbody>
      {this.state.data.map((element)=>(
          <tr>

            <td>{element.id}</td>
            <td> {element.name}</td>
            <td>{element.email}</td>
            <td>{element.phone}</td>
            <td>{element.address}</td>
            <td> <Button color="primary">Editar</Button></td>
            <td> <Button color="danger">Eliminar</Button></td>



          </tr>

        ))}
      </tbody>
    </Table>
    </Container>

    </>
    )

  }

}

export default App;
